mapper.select:
SELECT DISTINCT m.* FROM sys_menu m LEFT JOIN sys_role_menu rm ON m.id = rm.menu_id WHERE rm.role_id IN
#{roleId}
AND m.is_deleted = 0 AND m.status = 1 AND rm.is_deleted = 0 ORDER BY m.sort ASC

SELECT DISTINCT m.perms FROM sys_menu m LEFT JOIN sys_role_menu rm ON m.id = rm.menu_id WHERE rm.role_id IN
#{roleId}
AND m.perms IS NOT NULL AND m.perms != '' AND m.is_deleted = 0 AND rm.is_deleted = 0
